var isInitialized=false;var firebaseRef=new Firebase("https://amber-inferno-5910.firebaseio.com/");firebaseRef.onAuth(function(a){if(a&&!isInitialized){initChat(a);$("#container_demo").hide();$("#firechat-wrapper").show()}});function registerUser(){var b=document.getElementById("registerUsername").value;if(comparePasswords()){var a=document.getElementById("registerPassword").value}firebaseRef.createUser({email:b,password:a},function(d,c){if(d){alert(d,d)}else{console.log("Successfully created user account with uid:",c.uid);loginUser(b,a)}})}function loginUser(b,a){if(checkVariable(b)){var b=document.getElementById("loginUsername").value}if(checkVariable(a)){var a=document.getElementById("loginPassword").value}firebaseRef.authWithPassword({email:b,password:a},function(c,d){if(c){alert("Login Failed! "+c,c)}else{console.log("Authenticated successfully with payload:",d);$("#container_demo").hide();$("#firechat-wrapper").show()}},{remember:checkLoginTerm()})}function initChat(b){var a=new FirechatUI(firebaseRef,document.getElementById("firechat-wrapper"));a.setUser(b.uid,b[b.provider].email)}function checkVariable(a){if(typeof a!=="undefined"){return false}return true}function checkLoginTerm(){var a;if(document.getElementById("loginkeeping").checked){a="default"}else{a="sessionOnly"}return a}function comparePasswords(){if($("#passwordsignup").val()!=$("#registerPassword").val()){alert("Passwords do not match.");return false}return true};